\begin{exercise}{Demo2}{Product Rule}{0000}
\begin{exerciseStatement}

 Explain how to find the derivative \(f'(x)\). 

\[
          f(x)=-4 \, e^{x} \sin\left(x\right)\]\end{exerciseStatement}
\begin{exerciseAnswer}\[
          f'(x)=-4 \, \cos\left(x\right) e^{x} - 4 \, e^{x} \sin\left(x\right)\]\end{exerciseAnswer}
\end{exercise}

\begin{exercise}{Demo2}{Product Rule}{0001}
\begin{exerciseStatement}

 Explain how to find the derivative \(f'(x)\). 

\[
          f(x)=-4 \, \log\left(x\right) \sin\left(x\right)\]\end{exerciseStatement}
\begin{exerciseAnswer}\[
          f'(x)=-4 \, \cos\left(x\right) \log\left(x\right) - \frac{4 \, \sin\left(x\right)}{x}\]\end{exerciseAnswer}
\end{exercise}

\begin{exercise}{Demo2}{Product Rule}{0002}
\begin{exerciseStatement}

 Explain how to find the derivative \(f'(x)\). 

\[
          f(x)=3 \, \cos\left(x\right) \sin\left(x\right)\]\end{exerciseStatement}
\begin{exerciseAnswer}\[
          f'(x)=3 \, \cos\left(x\right)^{2} - 3 \, \sin\left(x\right)^{2}\]\end{exerciseAnswer}
\end{exercise}

\begin{exercise}{Demo2}{Product Rule}{0003}
